import React, { useState, useEffect, useRef } from 'react';

export default function VirtualStudyBuddy() {
  const [time, setTime] = useState(1500);
  const [isRunning, setIsRunning] = useState(false);
  const [tasks, setTasks] = useState([{ name: 'Complete Math Homework', completed: false }]);
  const [notes, setNotes] = useState([]);
  const [noteInput, setNoteInput] = useState('');
  const [quote, setQuote] = useState('The secret of getting ahead is getting started.');
  const intervalRef = useRef(null);

  const quotes = [
    "The secret of getting ahead is getting started.",
    "You don’t have to be great to start, but you have to start to be great.",
    "Success is the sum of small efforts, repeated day in and day out.",
    "The future depends on what you do today.",
    "Believe you can and you're halfway there.",
    "Don’t watch the clock; do what it does. Keep going.",
    "Difficulties in life are intended to make us better, not bitter.",
    "A little progress each day adds up to big results.",
    "Failure is the opportunity to begin again more intelligently.",
    "Your only limit is your mind. Keep pushing forward!"
  ];

  useEffect(() => {
    if (isRunning) {
      intervalRef.current = setInterval(() => {
        setTime(prev => {
          if (prev <= 1) {
            clearInterval(intervalRef.current);
            alert("Time’s up! Great job!");
            setIsRunning(false);
            return 1500;
          }
          return prev - 1;
        });
      }, 1000);
    }
    return () => clearInterval(intervalRef.current);
  }, [isRunning]);

  const formatTime = () => {
    const minutes = Math.floor(time / 60);
    const seconds = time % 60;
    return `${minutes}:${seconds < 10 ? '0' : ''}${seconds}`;
  };

  const startTimer = () => setIsRunning(true);
  const pauseTimer = () => {
    clearInterval(intervalRef.current);
    setIsRunning(false);
  };
  const resetTimer = () => {
    pauseTimer();
    setTime(1500);
  };

  const addTask = () => {
    const taskName = prompt("Enter task name:");
    if (taskName?.trim()) {
      setTasks([...tasks, { name: taskName.trim(), completed: false }]);
    } else {
      alert("Task name cannot be empty!");
    }
  };

  const toggleTask = index => {
    const updated = tasks.map((task, i) => i === index ? { ...task, completed: !task.completed } : task);
    setTasks(updated);
  };

  const clearCompleted = () => {
    setTasks(tasks.filter(task => !task.completed));
  };

  const updateProgress = () => {
    const completed = tasks.filter(task => task.completed).length;
    return tasks.length ? (completed / tasks.length) * 100 : 0;
  };

  const newQuote = () => {
    const random = quotes[Math.floor(Math.random() * quotes.length)];
    setQuote(random);
  };

  const saveNote = () => {
    if (noteInput.trim()) {
      setNotes([...notes, noteInput]);
      setNoteInput('');
    }
  };

  const clearNotes = () => setNotes([]);

  return (
    <div className="p-8 bg-gradient-to-br from-gray-100 to-blue-100 min-h-screen text-gray-800 font-sans">
      <div className="grid grid-cols-1 md:grid-cols-3 gap-8 max-w-7xl mx-auto">

        {/* Progress + Quotes Section */}
        <div className="space-y-8">
          <div className="bg-white p-6 rounded-2xl shadow-xl">
            <h1 className="text-3xl font-bold text-blue-900 mb-6">📘 Study Buddy</h1>

            <div className="mb-6">
              <h2 className="text-lg font-semibold mb-2">📊 Today's Progress</h2>
              <div className="w-full h-5 bg-gray-200 rounded-full overflow-hidden">
                <div className="h-full bg-green-500 transition-all duration-300" style={{ width: `${updateProgress()}%` }}></div>
              </div>
              <p className="text-sm mt-2 text-right font-medium">{Math.round(updateProgress())}% Complete</p>
            </div>

            <div>
              <h2 className="text-lg font-semibold mb-2">💡 Motivation</h2>
              <p className="italic text-gray-600 mb-4">"{quote}"</p>
              <button className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded-full text-sm transition" onClick={newQuote}>✨ New Quote</button>
            </div>
          </div>
        </div>

        {/* Timer + Task + Notes Section */}
        <div className="md:col-span-2 space-y-8">

          {/* Timer */}
          <div className="bg-white p-6 rounded-2xl shadow-xl text-center">
            <h2 className="text-2xl font-semibold mb-4">⏱️ Study Timer</h2>
            <div className="text-5xl text-red-500 font-extrabold mb-6">{formatTime()}</div>
            <div className="flex justify-center gap-4 flex-wrap">
              <button className="bg-green-500 hover:bg-green-600 text-white px-6 py-2 rounded-full" onClick={startTimer}>Start</button>
              <button className="bg-yellow-400 hover:bg-yellow-500 text-white px-6 py-2 rounded-full" onClick={pauseTimer}>Pause</button>
              <button className="bg-gray-400 hover:bg-gray-500 text-white px-6 py-2 rounded-full" onClick={resetTimer}>Reset</button>
            </div>
          </div>

          {/* Task Manager */}
          <div className="bg-white p-6 rounded-2xl shadow-xl">
            <h2 className="text-2xl font-semibold mb-6">📋 Task Manager</h2>
            <div className="flex gap-4 mb-6">
              <button className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded-full" onClick={addTask}>Add Task</button>
              <button className="bg-red-400 hover:bg-red-500 text-white px-4 py-2 rounded-full" onClick={clearCompleted}>Clear Completed</button>
            </div>
            <ul className="space-y-2">
              {tasks.map((task, idx) => (
                <li key={idx} className="flex items-center gap-3">
                  <input type="checkbox" checked={task.completed} onChange={() => toggleTask(idx)} className="w-5 h-5" />
                  <span className={`text-lg ${task.completed ? 'line-through text-gray-500' : 'text-gray-800'}`}>{task.name}</span>
                </li>
              ))}
            </ul>
          </div>

          {/* Notes */}
          <div className="bg-white p-6 rounded-2xl shadow-xl">
            <h2 className="text-2xl font-semibold mb-4">📝 Notes</h2>
            <textarea
              className="w-full border border-gray-300 p-3 rounded-lg mb-4 focus:outline-none focus:ring-2 focus:ring-blue-300"
              rows={5}
              value={noteInput}
              onChange={e => setNoteInput(e.target.value)}
              placeholder="Write your notes here..."
            ></textarea>
            <div className="flex gap-4 mb-4">
              <button className="bg-green-500 hover:bg-green-600 text-white px-4 py-2 rounded-full" onClick={saveNote}>Save Note</button>
              <button className="bg-gray-400 hover:bg-gray-500 text-white px-4 py-2 rounded-full" onClick={clearNotes}>Clear Notes</button>
            </div>
            <h3 className="text-lg font-semibold mb-2">📌 Saved Notes</h3>
            <ul className="list-disc list-inside text-gray-700 space-y-1">
              {notes.map((note, idx) => (
                <li key={idx}>{note}</li>
              ))}
            </ul>
          </div>

        </div>
      </div>
    </div>
  );
}
