import React from 'react';
import { Link } from 'react-router-dom';

function Login() {
  return (
    <div style={styles.background}>
      <div style={styles.card}>
        <h2 style={styles.heading}>üîê Welcome Back</h2>
        <p style={styles.subheading}>Login to continue</p>

        <input type="email" placeholder="üìß Email" style={styles.input} /><br />
        <input type="password" placeholder="üîí Password" style={styles.input} /><br />

        <div style={styles.rememberContainer}>
          <input type="checkbox" id="remember" />
          <label htmlFor="remember" style={styles.rememberLabel}>Remember me</label>
        </div>

        <button style={styles.button}>Login</button>

        <p style={styles.signupText}>
          Don't have an account? <Link to="/signup" style={styles.link}>Sign up</Link>
        </p>
      </div>
    </div>
  );
}

const styles = {
  background: {
    height: '100vh',
    background: 'linear-gradient(135deg, #2193b0, #6dd5ed)',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    fontFamily: "'Segoe UI', sans-serif",
  },
  card: {
    background: 'rgba(255, 255, 255, 0.1)',
    boxShadow: '0 8px 32px rgba(0, 0, 0, 0.2)',
    backdropFilter: 'blur(12px)',
    borderRadius: '20px',
    padding: '40px',
    textAlign: 'center',
    color: '#fff',
    width: '350px',
  },
  heading: {
    marginBottom: '10px',
    fontSize: '28px',
  },
  subheading: {
    fontSize: '14px',
    marginBottom: '20px',
    opacity: 0.8,
  },
  input: {
    padding: '12px',
    margin: '10px 0',
    width: '100%',
    borderRadius: '8px',
    border: 'none',
    outline: 'none',
    fontSize: '16px',
  },
  rememberContainer: {
    textAlign: 'left',
    marginBottom: '10px',
  },
  rememberLabel: {
    marginLeft: '8px',
    fontSize: '14px',
    opacity: 0.9,
  },
  button: {
    padding: '12px 20px',
    border: 'none',
    borderRadius: '8px',
    fontSize: '16px',
    cursor: 'pointer',
    backgroundColor: '#ffffff',
    color: '#2193b0',
    fontWeight: 'bold',
    width: '100%',
    marginTop: '10px',
    transition: '0.3s'
  },
  signupText: {
    marginTop: '20px',
    fontSize: '14px',
  },
  link: {
    color: '#fff',
    textDecoration: 'underline',
  }
};

export default Login;
