import React, { useState } from 'react';
import { Link } from 'react-router-dom';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = () => {
    console.log('User logged in:', { email, password });
    // Add actual login logic here
  };

  return (
    <div style={styles.background}>
      <div style={styles.card}>
        <h2 style={styles.heading}>ðŸŽ¯ Welcome Back!</h2>
        <p style={styles.subheading}>Ready to continue your journey?</p>

        <input
          type="email"
          placeholder="ðŸ“§ Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          style={styles.input}
        /><br />

        <input
          type="password"
          placeholder="ðŸ”’ Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          style={styles.input}
        /><br />

        <button style={styles.button} onClick={handleLogin}>ðŸŽ® Login</button>

        <p style={styles.signupText}>
          Don't have an account? <Link to="/signup" style={styles.link}>âš¡ Sign Up</Link>
        </p>
      </div>
    </div>
  );
}

const styles = {
  background: {
    height: '100vh',
    background: 'linear-gradient(135deg, #ff758c, #ff7eb3)',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    fontFamily: "'Segoe UI', sans-serif",
  },
  card: {
    background: 'rgba(255, 255, 255, 0.15)',
    boxShadow: '0 8px 32px rgba(31, 38, 135, 0.37)',
    backdropFilter: 'blur(10px)',
    borderRadius: '20px',
    padding: '40px',
    textAlign: 'center',
    color: '#fff',
    width: '350px',
  },
  heading: {
    marginBottom: '10px',
    fontSize: '28px',
  },
  subheading: {
    fontSize: '14px',
    marginBottom: '20px',
    opacity: 0.8
  },
  input: {
    padding: '12px',
    margin: '10px 0',
    width: '100%',
    borderRadius: '8px',
    border: 'none',
    outline: 'none',
    fontSize: '16px',
  },
  button: {
    padding: '12px 20px',
    border: 'none',
    borderRadius: '8px',
    fontSize: '16px',
    cursor: 'pointer',
    backgroundColor: '#ffffff',
    color: '#ff758c',
    fontWeight: 'bold',
    transition: '0.3s',
  },
  signupText: {
    marginTop: '20px',
    fontSize: '14px',
  },
  link: {
    color: '#fff',
    textDecoration: 'underline',
  }
};

export default Login;
