import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';

function Signup() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [rememberMe, setRememberMe] = useState(false);
  const navigate = useNavigate();

  const handleSignup = () => {
    console.log('User signed up:', { email, password, rememberMe });
    navigate('/');
  };

  return (
    <div style={styles.background}>
      <div style={styles.card}>
        <h2 style={styles.heading}>ðŸŽ® Join the Adventure!</h2>
        <p style={styles.subheading}>Create your account to start the quest</p>
        
        <input
          type="email"
          placeholder="ðŸ“§ Username or Email"
          style={styles.input}
          onChange={(e) => setEmail(e.target.value)}
        /><br />
        
        <input
          type="password"
          placeholder="ðŸ”’ Password"
          style={styles.input}
          onChange={(e) => setPassword(e.target.value)}
        /><br />
        
        <div style={styles.checkboxContainer}>
          <input
            type="checkbox"
            checked={rememberMe}
            onChange={() => setRememberMe(!rememberMe)}
          />
          <label style={styles.checkboxLabel}> Remember me</label>
        </div>

        <button onClick={handleSignup} style={styles.button}>ðŸš€ Sign Up</button>

        <p style={styles.loginText}>
          Already a member? <Link to="/" style={styles.link}>âœ¨ Login</Link>
        </p>
      </div>
    </div>
  );
}

const styles = {
  background: {
    height: '100vh',
    background: 'linear-gradient(135deg, #667eea, #764ba2)',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    fontFamily: "'Segoe UI', sans-serif",
  },
  card: {
    background: 'rgba(255, 255, 255, 0.15)',
    boxShadow: '0 8px 32px rgba(31, 38, 135, 0.37)',
    backdropFilter: 'blur(10px)',
    borderRadius: '20px',
    padding: '40px',
    textAlign: 'center',
    color: '#fff',
    width: '350px',
  },
  heading: {
    marginBottom: '10px',
    fontSize: '28px',
  },
  subheading: {
    fontSize: '14px',
    marginBottom: '20px',
    opacity: 0.8
  },
  input: {
    padding: '12px',
    margin: '10px 0',
    width: '100%',
    borderRadius: '8px',
    border: 'none',
    outline: 'none',
    fontSize: '16px',
  },
  checkboxContainer: {
    display: 'flex',
    alignItems: 'center',
    marginBottom: '15px',
  },
  checkboxLabel: {
    marginLeft: '8px',
    fontSize: '14px',
  },
  button: {
    padding: '12px 20px',
    border: 'none',
    borderRadius: '8px',
    fontSize: '16px',
    cursor: 'pointer',
    backgroundColor: '#ffffff',
    color: '#764ba2',
    fontWeight: 'bold',
    transition: '0.3s',
  },
  loginText: {
    marginTop: '20px',
    fontSize: '14px',
  },
  link: {
    color: '#fff',
    textDecoration: 'underline',
  }
};

export default Signup;
