import React, { useState, useEffect } from 'react';
import './Dashboard.css';

const Dashboard = ({ username }) => {
  const [subject, setSubject] = useState('');
  const [subjects, setSubjects] = useState([]);
  const [selectedSubject, setSelectedSubject] = useState('');
  const [unit, setUnit] = useState('');
  const [note, setNote] = useState('');
  const [quickNote, setQuickNote] = useState('');
  const [timer, setTimer] = useState(0);
  const [isRunning, setIsRunning] = useState(false);
  const [showUploadPopup, setShowUploadPopup] = useState(false);

  useEffect(() => {
    let interval;
    if (isRunning) {
      interval = setInterval(() => {
        setTimer((prev) => prev + 1);
      }, 1000);
    } else if (!isRunning && timer !== 0) {
      clearInterval(interval);
    }
    return () => clearInterval(interval);
  }, [isRunning, timer]);

  const handleAddSubject = () => {
    if (subject) {
      setSubjects([...subjects, subject]);
      setSubject('');
    }
  };

  const formatTime = (time) => {
    const getSeconds = `0${time % 60}`.slice(-2);
    const minutes = Math.floor(time / 60);
    const getMinutes = `0${minutes % 60}`.slice(-2);
    const getHours = `0${Math.floor(time / 3600)}`.slice(-2);
    return `${getHours}:${getMinutes}:${getSeconds}`;
  };

  return (
    <div className="dashboard-container">
      <nav className="dashboard-nav">
        <div className="logo">âš¡ AI Study Buddy</div>
        <div className="user-info">Hello!! {username}</div>
      </nav>

      <div className="dashboard-main">
        <div className="sidebar">
          <h3>Subjects</h3>
          <input
            type="text"
            placeholder="Enter subject name"
            value={subject}
            onChange={(e) => setSubject(e.target.value)}
          />
          <button onClick={handleAddSubject}>Add Subject</button>
          <div className="subject-list">
            {subjects.map((subj, index) => (
              <div key={index} onClick={() => setSelectedSubject(subj)} className="subject-item">
                {subj}
              </div>
            ))}
          </div>

          <div className="quick-notes">
            <h4>Quick Notes</h4>
            <input
              type="text"
              placeholder="Enter quick note"
              value={quickNote}
              onChange={(e) => setQuickNote(e.target.value)}
            />
            <button>Add Note</button>
          </div>
        </div>

        <div className="main-section">
          <h2>Subject Details</h2>
          <label>Choose a Subject:</label>
          <select value={selectedSubject} onChange={(e) => setSelectedSubject(e.target.value)}>
            <option value="">-- select --</option>
            {subjects.map((subj, index) => (
              <option key={index} value={subj}>{subj}</option>
            ))}
          </select>

          <input
            type="text"
            placeholder="Enter unit name"
            value={unit}
            onChange={(e) => setUnit(e.target.value)}
          />
          <button>Add Unit</button>

          <input
            type="text"
            placeholder="Enter note"
            value={note}
            onChange={(e) => setNote(e.target.value)}
          />
          <button>Add Note</button>

          <div className="upload-section">
            <button onClick={() => setShowUploadPopup(true)}>+ Upload Note</button>
          </div>

          {showUploadPopup && (
            <div className="upload-popup">
              <div className="popup-content">
                <input type="file" />
                <button onClick={() => setShowUploadPopup(false)}>Close</button>
              </div>
            </div>
          )}

          <div className="categories">
            <strong>PDFs</strong> | <strong>Docs</strong> | <strong>Images</strong> | <strong>Videos</strong>
          </div>
        </div>

        <div className="sidebar-right">
          <h3>Calendar</h3>
          <p>Click to schedule study time</p>

          <h4>Study Timer</h4>
          <div className="timer-display">{formatTime(timer)}</div>
          <button onClick={() => setIsRunning(true)}>Start Timer</button>
          <button className="reset-btn" onClick={() => { setIsRunning(false); setTimer(0); }}>Reset</button>
        </div>
      </div>

      <div className="chatbot-button">
        ðŸ’¬
      </div>
    </div>
  );
};

export default Dashboard;
