import React, { useState, useRef } from "react";
import "./Dashboard.css";  // Import the CSS file

export default function Dashboard() {
  // State for Subjects and Notes
  const [subjects, setSubjects] = useState([]);
  const [notes, setNotes] = useState([]);
  
  // State for input values
  const [subjectInput, setSubjectInput] = useState("");
  const [noteInput, setNoteInput] = useState("");
  const [unitInput, setUnitInput] = useState("");
  const [centerNoteInput, setCenterNoteInput] = useState("");
  
  // For subject selection in main content
  const [selectedSubjectName, setSelectedSubjectName] = useState("");
  
  // For tracking which context menu (subject list) is open
  const [openContextIndex, setOpenContextIndex] = useState(null);
  // For tracking context menu in quick notes
  const [openNoteContextIndex, setOpenNoteContextIndex] = useState(null);
  
  // Timer state and reference for interval
  const [timerSeconds, setTimerSeconds] = useState(0);
  const timerId = useRef(null);
  
  // AI Assistant states
  const [aiQuestion, setAiQuestion] = useState("");
  const [aiResponse, setAiResponse] = useState("");

  // Functions for Subjects
  const addSubject = () => {
    const subjectName = subjectInput.trim();
    if (subjectName && !subjects.some(s => s.name === subjectName)) {
      const newSubject = { name: subjectName, units: [], notes: [] };
      setSubjects([...subjects, newSubject]);
      setSubjectInput("");
    }
  };

  const removeSubject = (index) => {
    const newSubjects = [...subjects];
    newSubjects.splice(index, 1);
    setSubjects(newSubjects);
    if (selectedSubjectName === subjects[index].name) {
      setSelectedSubjectName("");
    }
  };

  // Functions for Quick Notes
  const addNote = () => {
    const noteText = noteInput.trim();
    if (noteText) {
      setNotes([...notes, noteText]);
      setNoteInput("");
    }
  };

  const removeNote = (index) => {
    const newNotes = [...notes];
    newNotes.splice(index, 1);
    setNotes(newNotes);
  };

  // Main Content: Add Unit and Center Notes to Selected Subject
  const getSelectedSubject = () => {
    return subjects.find(s => s.name === selectedSubjectName);
  };

  const addUnit = () => {
    const unitName = unitInput.trim();
    if (selectedSubjectName && unitName) {
      setSubjects(subjects.map(subject => 
        subject.name === selectedSubjectName
          ? { ...subject, units: [...subject.units, unitName] }
          : subject
      ));
      setUnitInput("");
    }
  };

  const addCenterNote = () => {
    const note = centerNoteInput.trim();
    if (selectedSubjectName && note) {
      setSubjects(subjects.map(subject => 
        subject.name === selectedSubjectName
          ? { ...subject, notes: [...subject.notes, note] }
          : subject
      ));
      setCenterNoteInput("");
    }
  };

  // Timer functions
  const startTimer = () => {
    if (!timerId.current) {
      timerId.current = setInterval(() => {
        setTimerSeconds(prev => prev + 1);
      }, 1000);
    }
  };

  const resetTimer = () => {
    if (timerId.current) {
      clearInterval(timerId.current);
      timerId.current = null;
    }
    setTimerSeconds(0);
  };

  const formatTime = (seconds) => {
    const hours = String(Math.floor(seconds / 3600)).padStart(2, "0");
    const minutes = String(Math.floor((seconds % 3600) / 60)).padStart(2, "0");
    const secs = String(seconds % 60).padStart(2, "0");
    return `${hours}:${minutes}:${secs}`;
  };

  // AI Assistant simulation
  const askAI = () => {
    const question = aiQuestion.trim();
    if (question) {
      setAiResponse(
        "This is a simulated response. In a real app, this would connect to an AI service."
      );
      setAiQuestion("");
    }
  };

  return (
    <div>
      <header>
        <h1>⚡ AI Study Buddy</h1>
        <div><strong>Profile Management</strong></div>
      </header>

      <div className="container">
        {/* Left Sidebar */}
        <div className="sidebar">
          {/* Subjects Section */}
          <div className="sidebar-section">
            <h3>Subjects</h3>
            <input 
              type="text" 
              placeholder="Enter subject name" 
              value={subjectInput}
              onChange={e => setSubjectInput(e.target.value)}
            />
            <button onClick={addSubject}>Add Subject</button>
            <ul className="item-list">
              {subjects.map((subject, index) => (
                <li key={index} className="list-item">
                  <span 
                    className="item-name" 
                    onClick={() => setSelectedSubjectName(subject.name)}
                  >
                    {subject.name}
                  </span>
                  <span 
                    className="menu-icon" 
                    onClick={() =>
                      setOpenContextIndex(openContextIndex === index ? null : index)
                    }
                  >
                    ⋮
                  </span>
                  {openContextIndex === index && (
                    <div className="context-menu" style={{ display: "block" }}>
                      <button 
                        onClick={() => {
                          setSelectedSubjectName(subject.name);
                          setOpenContextIndex(null);
                        }}
                      >
                        Open
                      </button>
                      <button 
                        className="remove"
                        onClick={() => {
                          removeSubject(index);
                          setOpenContextIndex(null);
                        }}
                      >
                        Remove
                      </button>
                    </div>
                  )}
                </li>
              ))}
            </ul>
          </div>

          {/* Quick Notes Section */}
          <div className="sidebar-section">
            <h3>Quick Notes</h3>
            <input 
              type="text" 
              placeholder="Enter quick note" 
              value={noteInput}
              onChange={e => setNoteInput(e.target.value)}
            />
            <button onClick={addNote}>Add Note</button>
            <ul className="item-list">
              {notes.map((note, index) => (
                <li key={index} className="list-item">
                  <span className="item-name">{note}</span>
                  <span 
                    className="menu-icon" 
                    onClick={() =>
                      setOpenNoteContextIndex(openNoteContextIndex === index ? null : index)
                    }
                  >
                    ⋮
                  </span>
                  {openNoteContextIndex === index && (
                    <div className="context-menu" style={{ display: "block" }}>
                      <button 
                        className="remove"
                        onClick={() => {
                          removeNote(index);
                          setOpenNoteContextIndex(null);
                        }}
                      >
                        Remove
                      </button>
                    </div>
                  )}
                </li>
              ))}
            </ul>
          </div>
        </div>

        {/* Main Content */}
        <div className="main">
          <h2>Subject Details</h2>
          <label htmlFor="subjectSelect">Choose a Subject:</label>
          <select 
            id="subjectSelect" 
            value={selectedSubjectName}
            onChange={(e) => setSelectedSubjectName(e.target.value)}
          >
            <option value="">-- Select Subject --</option>
            {subjects.map((subject, idx) => (
              <option key={idx} value={subject.name}>
                {subject.name}
              </option>
            ))}
          </select>

          <h3>Add Unit</h3>
          <input 
            type="text" 
            placeholder="Enter unit name" 
            value={unitInput}
            onChange={(e) => setUnitInput(e.target.value)}
          />
          <button onClick={addUnit}>Add Unit</button>
          <ul className="details-list">
            {getSelectedSubject() &&
              getSelectedSubject().units.map((unit, idx) => <li key={idx}>{unit}</li>)}
          </ul>

          <h3>Add Notes</h3>
          <input 
            type="text" 
            placeholder="Enter note" 
            value={centerNoteInput}
            onChange={(e) => setCenterNoteInput(e.target.value)}
          />
          <button onClick={addCenterNote}>Add Note</button>
          <ul className="details-list">
            {getSelectedSubject() &&
              getSelectedSubject().notes.map((note, idx) => <li key={idx}>{note}</li>)}
          </ul>

          <div className="footer-tabs">
            <div><strong>PDFs</strong></div>
            <div><strong>Docs</strong></div>
            <div><strong>Images</strong></div>
            <div><strong>Videos</strong></div>
          </div>
        </div>

        {/* Right Sidebar */}
        <div className="rightbar">
          <h3>Calendar</h3>
          <p>Click to schedule study time</p>

          <h3>Study Timer</h3>
          <div id="timerDisplay">{formatTime(timerSeconds)}</div>
          <button onClick={startTimer}>Start Timer</button>
          <button 
            onClick={resetTimer} 
            style={{ marginTop: "5px", backgroundColor: "var(--danger-color)" }}
          >
            Reset
          </button>

          <h3>AI Assistant</h3>
          <input 
            type="text" 
            placeholder="Ask me anything..." 
            value={aiQuestion}
            onChange={(e) => setAiQuestion(e.target.value)}
          />
          <button onClick={askAI} style={{ marginTop: "5px" }}>Ask</button>
          <div id="aiResponse">{aiResponse}</div>
        </div>
      </div>
    </div>
  );
}
