import React, { useState, useEffect } from 'react';

function Dashboard() {
  const user = localStorage.getItem('username');
  const [minutes, setMinutes] = useState('');
  const [secondsLeft, setSecondsLeft] = useState(0);
  const [isRunning, setIsRunning] = useState(false);

  const handleStart = () => {
    const totalSeconds = parseInt(minutes) * 60;
    if (isNaN(totalSeconds) || totalSeconds <= 0) {
      alert('Please enter a valid number of minutes.');
      return;
    }
    setSecondsLeft(totalSeconds);
    setIsRunning(true);
  };

  useEffect(() => {
    let timer;
    if (isRunning && secondsLeft > 0) {
      timer = setTimeout(() => setSecondsLeft(secondsLeft - 1), 1000);
    } else if (secondsLeft === 0 && isRunning) {
      setIsRunning(false);
      alert("‚è∞ Time's up!");
    }
    return () => clearTimeout(timer);
  }, [isRunning, secondsLeft]);

  const formatTime = () => {
    const mins = Math.floor(secondsLeft / 60);
    const secs = secondsLeft % 60;
    return `${String(mins).padStart(2, '0')}:${String(secs).padStart(2, '0')}`;
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.heading}>üëã Hello, {user}!</h1>
      <p style={styles.subheading}>Let's get some work done! üí™</p>

      <div style={styles.card}>
        <label style={styles.label}>Set Timer (minutes):</label>
        <input
          type="number"
          value={minutes}
          onChange={(e) => setMinutes(e.target.value)}
          style={styles.input}
        />
        <button onClick={handleStart} style={styles.button}>Start Timer</button>

        {isRunning && <p style={styles.timer}>‚è≥ {formatTime()}</p>}
      </div>
    </div>
  );
}

const styles = {
  container: {
    height: '100vh',
    background: 'linear-gradient(135deg, #0f2027, #203a43, #2c5364)',
    color: '#fff',
    fontFamily: "'Segoe UI', sans-serif",
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    paddingTop: '60px',
  },
  heading: {
    fontSize: '32px',
    fontWeight: 'bold',
    marginBottom: '10px',
  },
  subheading: {
    fontSize: '18px',
    marginBottom: '30px',
    color: '#ccc',
  },
  card: {
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
    padding: '30px',
    borderRadius: '20px',
    boxShadow: '0 8px 32px rgba(0, 0, 0, 0.37)',
    textAlign: 'center',
    width: '320px',
  },
  label: {
    fontSize: '16px',
    display: 'block',
    marginBottom: '10px',
  },
  input: {
    width: '100%',
    padding: '10px',
    borderRadius: '8px',
    border: 'none',
    marginBottom: '15px',
    fontSize: '16px',
    background: '#1e2a38',
    color: '#fff',
  },
  button: {
    padding: '12px',
    width: '100%',
    border: 'none',
    borderRadius: '8px',
    backgroundColor: '#00b4db',
    color: '#fff',
    fontSize: '16px',
    fontWeight: 'bold',
    cursor: 'pointer',
    marginBottom: '15px',
  },
  timer: {
    fontSize: '24px',
    marginTop: '10px',
    fontWeight: 'bold',
  },
};

export default Dashboard;
