// Dashboard.js
import React, { useState, useRef } from "react";
import "./Dashboard.css";

const Dashboard = ({ userName }) => {
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [chatOpen, setChatOpen] = useState(false);
  const fileInputRef = useRef(null);

  const handleFileUpload = (event) => {
    const files = Array.from(event.target.files);
    setSelectedFiles(prev => [...prev, ...files]);
  };

  const openFileDialog = () => {
    fileInputRef.current.click();
  };

  const toggleChat = () => {
    setChatOpen(!chatOpen);
  };

  return (
    <div className="dashboard-container">
      <header>
        <h1>AI Study Buddy</h1>
        <div className="profile-greet">Hello!! {userName}</div>
      </header>

      <main className="dashboard-main">
        <div className="left-panel">
          <div className="subject-section">
            <input type="text" placeholder="Enter subject name" />
            <button>Add Subject</button>
            <div className="subject-list">
              <div className="subject-item">dbms</div>
              <div className="subject-item">ml</div>
            </div>
          </div>

          <div className="quick-notes">
            <input type="text" placeholder="Enter quick note" />
            <button>Add Note</button>
          </div>
        </div>

        <div className="center-panel">
          <div className="subject-details">
            <label>Choose a Subject:</label>
            <select>
              <option>dbms</option>
              <option>ml</option>
            </select>

            <input type="text" placeholder="Enter unit name" />
            <button>Add Unit</button>

            <input type="text" placeholder="Enter note" />
            <button>Add Note</button>

            <button className="upload-button" onClick={openFileDialog}>+ Upload Note</button>
            <input
              type="file"
              multiple
              ref={fileInputRef}
              onChange={handleFileUpload}
              style={{ display: "none" }}
            />

            <div className="uploaded-files">
              {selectedFiles.map((file, index) => (
                <div key={index}>{file.name}</div>
              ))}
            </div>
          </div>
        </div>

        <div className="right-panel">
          <div className="calendar">Calendar (coming soon)</div>
          <div className="timer-box">
            <h3>Study Timer</h3>
            <div className="timer">00:00:00</div>
            <button>Start Timer</button>
            <button className="reset">Reset</button>
          </div>
        </div>

        <div className={`chatbot-button ${chatOpen ? "active" : ""}`} onClick={toggleChat}>
          💬 Chat
        </div>

        {chatOpen && (
          <div className="chatbot-popup">
            <div className="chatbot-header">AI Assistant</div>
            <textarea placeholder="Ask me anything..."></textarea>
            <button>Ask</button>
          </div>
        )}
      </main>
    </div>
  );
};

export default Dashboard;
