import React, { useState, useEffect, useRef } from 'react';
import './Dashboard.css';
import { evaluate } from 'mathjs';

const Dashboard = ({ username }) => {
  const [expression, setExpression] = useState('');
  const [result, setResult] = useState('');
  const [notes, setNotes] = useState([]);
  const [newNote, setNewNote] = useState('');
  const [reminder, setReminder] = useState({ date: '', time: '', note: '' });
  const [reminders, setReminders] = useState([]);
  const [showChatbot, setShowChatbot] = useState(false);
  const [timeLeft, setTimeLeft] = useState(1500); // Default 25 min in seconds
  const timerRef = useRef(null);

  const handleAddNote = () => {
    if (newNote.trim() !== '') {
      setNotes([...notes, newNote]);
      setNewNote('');
    }
  };

  const handleAddReminder = () => {
    if (reminder.date && reminder.time && reminder.note.trim() !== '') {
      setReminders([...reminders, reminder]);
      setReminder({ date: '', time: '', note: '' });
    }
  };

  const startTimer = (duration) => {
    if (timerRef.current) clearInterval(timerRef.current);
    setTimeLeft(duration);
    timerRef.current = setInterval(() => {
      setTimeLeft(prev => {
        if (prev <= 1) {
          clearInterval(timerRef.current);
          return 0;
        }
        return prev - 1;
      });
    }, 1000);
  };

  const formatTime = (seconds) => {
    const min = Math.floor(seconds / 60).toString().padStart(2, '0');
    const sec = (seconds % 60).toString().padStart(2, '0');
    return `${min}:${sec}`;
  };

  const calculatorButtons = [
    ['x', 'y', 'z', 'π', '7', '8', '9', '*', '/'],
    ['x²', 'xʸ', '√', 'e', '4', '5', '6', '+', '-'],
    ['<', '>', '≤', '≥', '1', '2', '3', '=', '⌫'],
    ['(', ')', '|x|', ',', '0', '.', '↵']
  ];

  const insertSymbol = (symbol) => {
    if (symbol === '=') {
      try {
        let expr = expression
          .replace(/π/g, 'pi')
          .replace(/√/g, 'sqrt')
          .replace(/x²/g, 'pow(x,2)')
          .replace(/xʸ/g, 'pow(x,y)')
          .replace(/÷/g, '/')
          .replace(/×/g, '*')
          .replace(/\|x\|/g, 'abs(x)');
        const result = evaluate(expr);
        setResult(result);
      } catch (err) {
        setResult('Error');
      }
    } else if (symbol === '⌫') {
      setExpression(expression.slice(0, -1));
    } else if (symbol === '↵') {
      setExpression('');
      setResult('');
    } else {
      setExpression(expression + symbol);
    }
  };

  return (
    <div className="dashboard-container">
      <nav>
        <div className="logo">⚡ AI Study Buddy</div>
        <div><span className="welcome-text">Welcome, {username}!</span></div>
      </nav>

      <div className="dashboard-panels">
        {/* Left Panel */}
        <div className="panel left-panel">
          <h3>Scientific Calculator</h3>
          <input
            type="text"
            placeholder="Enter expression"
            value={expression}
            readOnly
          />
          <div className="calculator-grid">
            {calculatorButtons.map((row, i) => (
              <div key={i} className="calculator-row">
                {row.map((btn, j) => (
                  <button key={j} onClick={() => insertSymbol(btn)}>{btn}</button>
                ))}
              </div>
            ))}
          </div>
          <div className="calculator-result"><strong>Result:</strong> {result}</div>

          <h3 style={{ marginTop: '30px' }}>Notes</h3>
          <input
            type="text"
            placeholder="Enter a note"
            value={newNote}
            onChange={(e) => setNewNote(e.target.value)}
          />
          <button onClick={handleAddNote}>Add Note</button>
          <div className="items-list">
            {notes.map((note, idx) => (
              <div key={idx} className="card">📝 {note}</div>
            ))}
          </div>
        </div>

        {/* Middle Panel */}
        <div className="panel middle-panel">
          <h2>Your Study Dashboard</h2>
          <p>Add your notes and use the calculator to the left, and set reminders/timers to the right to stay productive!</p>
        </div>

        {/* Right Panel */}
        <div className="panel right-panel">
          <h3>Reminders</h3>
          <input
            type="date"
            value={reminder.date}
            onChange={(e) => setReminder({ ...reminder, date: e.target.value })}
          />
          <input
            type="time"
            value={reminder.time}
            onChange={(e) => setReminder({ ...reminder, time: e.target.value })}
          />
          <input
            type="text"
            placeholder="Reminder note"
            value={reminder.note}
            onChange={(e) => setReminder({ ...reminder, note: e.target.value })}
          />
          <button onClick={handleAddReminder}>Set Reminder</button>
          <div className="items-list">
            {reminders.map((rem, idx) => (
              <div key={idx} className="card">📅 {rem.date} ⏰ {rem.time} - {rem.note}</div>
            ))}
          </div>

          <h3 style={{ marginTop: '30px' }}>Quick Timer</h3>
          <div className="round-timer">
            <div className="timer-display">{formatTime(timeLeft)}</div>
            <div className="timer-options">
              <button onClick={() => startTimer(60)}>1 Min</button>
              <button onClick={() => startTimer(300)}>5 Min</button>
              <button onClick={() => {
                const hour = prompt('Enter number of hours:');
                if (hour) startTimer(parseInt(hour) * 3600);
              }}>Set Hourly Timer</button>
            </div>
          </div>
        </div>
      </div>

      <button className="chatbot-toggle" onClick={() => setShowChatbot(!showChatbot)}>💬</button>
      {showChatbot && (
        <div className="chatbot-popup">
          <p>Hi! I'm your AI Study Buddy chatbot. How can I help you today?</p>
          <input type="text" placeholder="Type your message..." />
          <button>Send</button>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
