import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';

function Dashboard() {
  const location = useLocation();
  const name = location.state?.name || 'User';

  const [task, setTask] = useState('');
  const [time, setTime] = useState(0);
  const [isRunning, setIsRunning] = useState(false);

  useEffect(() => {
    let interval;
    if (isRunning) {
      interval = setInterval(() => setTime((prev) => prev + 1), 1000);
    } else if (!isRunning && time !== 0) {
      clearInterval(interval);
    }
    return () => clearInterval(interval);
  }, [isRunning, time]);

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60).toString().padStart(2, '0');
    const secs = (seconds % 60).toString().padStart(2, '0');
    return `${mins}:${secs}`;
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.welcome}>Hello, {name}!</h1>
      <p>What would you like to work on today?</p>

      <input
        type="text"
        placeholder="Enter your task..."
        value={task}
        onChange={(e) => setTask(e.target.value)}
        style={styles.input}
      />

      <h2 style={styles.timer}>{formatTime(time)}</h2>
      <div>
        <button onClick={() => setIsRunning(true)} style={styles.button}>Start</button>
        <button onClick={() => setIsRunning(false)} style={styles.button}>Pause</button>
        <button onClick={() => { setTime(0); setIsRunning(false); }} style={styles.button}>Reset</button>
      </div>
    </div>
  );
}

const styles = {
  container: {
    textAlign: 'center',
    padding: '50px',
    fontFamily: 'Segoe UI, sans-serif',
    background: 'linear-gradient(135deg, #2193b0, #6dd5ed)',
    height: '100vh',
    color: '#fff'
  },
  welcome: {
    fontSize: '32px',
    marginBottom: '20px'
  },
  input: {
    padding: '12px',
    borderRadius: '8px',
    width: '300px',
    marginBottom: '20px',
    fontSize: '16px',
    border: 'none',
    outline: 'none'
  },
  timer: {
    fontSize: '48px',
    margin: '20px 0'
  },
  button: {
    padding: '10px 20px',
    margin: '0 5px',
    fontSize: '16px',
    borderRadius: '8px',
    border: 'none',
    cursor: 'pointer',
    backgroundColor: '#fff',
    color: '#2193b0',
    fontWeight: 'bold'
  }
};

export default Dashboard;
