import React from 'react';
import { Link } from 'react-router-dom';

function Login() {
  return (
    <div style={styles.background}>
      <div style={styles.card}>
        <h2 style={styles.heading}>üîê Welcome Back</h2>
        <p style={styles.subheading}>Login to continue</p>

        <input type="email" placeholder="üìß Email" style={styles.input} /><br />
        <input type="password" placeholder="üîí Password" style={styles.input} /><br />

        <div style={styles.rememberContainer}>
          <input type="checkbox" id="remember" />
          <label htmlFor="remember" style={styles.rememberLabel}>Remember me</label>
        </div>

        <button style={styles.button}>Login</button>

        <p style={styles.signupText}>
          Don't have an account? <Link to="/signup" style={styles.link}>Sign up</Link>
        </p>
      </div>
    </div>
  );
}

const styles = {
  ...require('./Signup').default.styles // Reuse styles from Signup if using module
};

export default Login;
