import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function EducationForm() {
  const navigate = useNavigate();
  const [username, setUsername] = useState('');
  const [degree, setDegree] = useState('');
  const [institution, setInstitution] = useState('');
  const [year, setYear] = useState('');

  useEffect(() => {
    const storedUsername = localStorage.getItem('username');
    if (!storedUsername) {
      alert('No user found. Please login.');
      navigate('/login');
    } else {
      setUsername(storedUsername);
    }
  }, [navigate]);

  const handleSubmit = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/education", {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify({
          degree: degree,
          college: institution, // corrected from 'college'
          year: year,
          username: username,
        })
      });

      const data = await res.json();
      if (!res.ok) throw new Error(data.message || "Error saving education");
      alert("Education saved!");
      navigate('/survey'); 
    } catch (err) {
      alert("Error saving education.");
    }
  };

  const handleSkip = async () => {
    try {
      await axios.post('http://localhost:5000/api/education', {
        username,
        skipped: true
      });
      alert("Skipped education!");
      navigate('/survey');
    } catch (err) {
      console.error(err);
      alert("Error skipping education.");
    }
  };

  return (
    <>
      <style>{`
        body {
          margin: 0;
          padding: 0;
          height: 100vh;
          background: #0c1125;
          display: flex;
          justify-content: center;
          align-items: center;
          font-family: Arial, sans-serif;
        }
        .form-container {
          background: #1c1f2f;
          padding: 30px;
          border-radius: 10px;
          box-shadow: 0 0 15px rgba(255, 255, 255, 0.1);
          text-align: center;
          color: white;
          width: 300px;
        }
        input {
          width: 90%;
          padding: 10px;
          margin: 10px 0;
          border-radius: 8px;
          border: none;
          outline: none;
          font-size: 14px;
        }
        button {
          width: 100%;
          padding: 10px;
          margin-top: 10px;
          background: orange;
          border: none;
          border-radius: 8px;
          cursor: pointer;
          color: white;
          font-weight: bold;
        }
        button:hover {
          background: #ff9500;
        }
      `}</style>

      <div className="form-container">
        <h2>Hi {username}, Enter Your Education Details</h2>
        <input type="text" placeholder="Degree" value={degree} onChange={(e) => setDegree(e.target.value)} />
        <input type="text" placeholder="Institution" value={institution} onChange={(e) => setInstitution(e.target.value)} />
        <input type="text" placeholder="Year" value={year} onChange={(e) => setYear(e.target.value)} />

        <button onClick={handleSubmit}>Submit</button>
        <button onClick={handleSkip}>Skip</button>
      </div>
    </>
  );
}

export default EducationForm;
