import React, { useState } from "react";

export default function Dashboard() {
  const [activeFeature, setActiveFeature] = useState("subjects");
  const [selectedSubject, setSelectedSubject] = useState("Computer Science");
  const [selectedUnit, setSelectedUnit] = useState("Data Structures & Algorithms");

  const features = [
    { id: "subjects", icon: "ðŸ“˜", label: "Subjects" },
    { id: "add", icon: "âž•", label: "Add New" },
    { id: "notes", icon: "ðŸ“", label: "My Notes" },
    { id: "addNotes", icon: "ðŸ“„", label: "Add Notes" },
    { id: "calendar", icon: "ðŸ“…", label: "Calendar" },
    { id: "timer", icon: "ðŸ•’", label: "Study Timer" },
    { id: "chatbot", icon: "ðŸ¤–", label: "AI Assistant" }
  ];

  const renderContent = () => {
    switch (activeFeature) {
      case "subjects":
        return (
          <div className="space-y-6">
            <div className="bg-white p-6 rounded-xl shadow-xs border border-gray-100">
              <h2 className="text-xl font-bold text-gray-800 mb-1">{selectedSubject}</h2>
              <p className="text-blue-600 font-medium">{selectedUnit}</p>
            </div>
            <div className="bg-white p-6 rounded-xl shadow-xs border border-gray-100">
              <h3 className="text-lg font-bold text-gray-800 mb-4">All Subjects</h3>
              <div className="space-y-3">
                {["Mathematics", "Physics", "History", "Literature"].map((subject) => (
                  <div 
                    key={subject}
                    className={`p-4 rounded-lg cursor-pointer ${selectedSubject === subject ? 'bg-blue-50 border border-blue-200' : 'bg-gray-50 hover:bg-gray-100'}`}
                    onClick={() => setSelectedSubject(subject)}
                  >
                    <p className="font-medium">{subject}</p>
                  </div>
                ))}
              </div>
            </div>
          </div>
        );
      case "notes":
        return (
          <div className="bg-white p-6 rounded-xl shadow-xs border border-gray-100">
            <h2 className="text-xl font-bold text-gray-800 mb-6">My Notes</h2>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              {["Lecture Notes 1", "Exam Prep", "Research Paper", "Summary"].map((note) => (
                <div key={note} className="p-4 bg-gray-50 rounded-lg border border-gray-200 hover:bg-gray-100">
                  <p className="font-medium">{note}</p>
                  <p className="text-sm text-gray-500 mt-1">Last edited: 2 days ago</p>
                </div>
              ))}
            </div>
          </div>
        );
      case "calendar":
        return (
          <div className="bg-white p-6 rounded-xl shadow-xs border border-gray-100">
            <h2 className="text-xl font-bold text-gray-800 mb-6">Study Calendar</h2>
            <div className="bg-gray-50 p-4 rounded-lg border border-gray-200">
              <p className="text-center text-gray-500">Calendar view will appear here</p>
              <p className="text-center text-sm text-gray-400 mt-2">Click date to allocate study time</p>
            </div>
          </div>
        );
      case "timer":
        return (
          <div className="bg-white p-6 rounded-xl shadow-xs border border-gray-100">
            <h2 className="text-xl font-bold text-gray-800 mb-6">Study Timer</h2>
            <div className="flex flex-col items-center">
              <div className="text-4xl font-mono mb-4">25:00</div>
              <button className="px-6 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700">
                Start Timer
              </button>
            </div>
          </div>
        );
      default:
        return (
          <div className="bg-white p-6 rounded-xl shadow-xs border border-gray-100">
            <h2 className="text-xl font-bold text-gray-800 mb-4">{features.find(f => f.id === activeFeature)?.label}</h2>
            <p className="text-gray-600">Content for {activeFeature} will appear here</p>
          </div>
        );
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 font-sans flex">
      {/* Left Sidebar - Navigation */}
      <div className="w-64 bg-white border-r border-gray-200 p-4">
        <div className="flex items-center space-x-3 mb-8 p-2">
          <div className="p-2 bg-blue-100 rounded-lg">
            <span className="text-blue-600 text-xl">âš¡</span>
          </div>
          <h1 className="text-xl font-bold text-gray-800">AI Study Buddy</h1>
        </div>
        
        <nav className="space-y-1">
          {features.map((feature) => (
            <button
              key={feature.id}
              className={`w-full flex items-center space-x-3 px-4 py-3 rounded-lg text-left ${activeFeature === feature.id ? 'bg-blue-50 text-blue-600' : 'text-gray-700 hover:bg-gray-100'}`}
              onClick={() => setActiveFeature(feature.id)}
            >
              <span className="text-lg">{feature.icon}</span>
              <span className="font-medium">{feature.label}</span>
            </button>
          ))}
        </nav>

        <div className="mt-auto pt-4 border-t border-gray-200">
          <button className="w-full flex items-center space-x-3 px-4 py-3 rounded-lg text-gray-700 hover:bg-gray-100">
            <span>ðŸ‘¤</span>
            <span className="font-medium">Profile Management</span>
          </button>
        </div>
      </div>

      {/* Main Content Area */}
      <div className="flex-1 p-8 overflow-auto">
        <div className="max-w-4xl mx-auto">
          {renderContent()}
        </div>
      </div>
    </div>
  );
}
