import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function EducationForm() {
  const navigate = useNavigate();
  const [username, setUsername] = useState('');
  const [degree, setDegree] = useState('');
  const [institution, setInstitution] = useState('');
  const [year, setYear] = useState('');

  useEffect(() => {
    const storedUsername = localStorage.getItem('username');
    if (!storedUsername) {
      alert('No user found. Please login.');
      navigate('/login');
    } else {
      setUsername(storedUsername);
    }
  }, [navigate]);

  const handleSubmit = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/education", {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify({
          degree,
          college: institution,
          year,
          username,
        })
      });

      const data = await res.json();
      if (!res.ok) throw new Error(data.message || "Error saving education");
      alert("Education saved!");
      navigate('/survey');
    } catch (err) {
      alert("Error saving education.");
    }
  };

  const handleSkip = async () => {
    try {
      await axios.post('http://localhost:5000/api/education', {
        username,
        skipped: true
      });
      alert("Skipped education!");
      navigate('/survey');
    } catch (err) {
      console.error(err);
      alert("Error skipping education.");
    }
  };

  return (
    <>
      <style>{`
        body {
          margin: 0;
          padding: 0;
          height: 100vh;
          background: linear-gradient(135deg, #0f2027, #203a43, #2c5364);
          display: flex;
          flex-direction: column;
          justify-content: center;
          align-items: center;
          font-family: 'Segoe UI', sans-serif;
        }
        .greeting {
          color: white;
          font-size: 22px;
          margin-bottom: 20px;
          font-weight: bold;
        }
        .form-container {
          background: rgba(0, 0, 0, 0.4);
          box-shadow: 0 8px 32px rgba(0, 0, 0, 0.37);
          backdrop-filter: blur(10px);
          border-radius: 20px;
          padding: 40px;
          text-align: center;
          color: #ffffff;
          width: 350px;
        }
        input {
          width: 100%;
          padding: 12px;
          margin: 10px 0;
          border-radius: 8px;
          border: none;
          outline: none;
          font-size: 16px;
          background: #1e2a38;
          color: #fff;
        }
        button {
          width: 100%;
          padding: 12px;
          margin-top: 10px;
          background-color: #00b4db;
          border: none;
          border-radius: 8px;
          cursor: pointer;
          color: white;
          font-weight: bold;
          font-size: 16px;
          transition: 0.3s;
        }
        button:hover {
          background-color: #009ac6;
        }
      `}</style>

      <div className="greeting">Hi {username}, please fill your education details ðŸ‘‡</div>

      <div className="form-container">
        <input type="text" placeholder="ðŸŽ“ Degree" value={degree} onChange={(e) => setDegree(e.target.value)} />
        <input type="text" placeholder="ðŸ« Institution" value={institution} onChange={(e) => setInstitution(e.target.value)} />
        <input type="text" placeholder="ðŸ“… Year" value={year} onChange={(e) => setYear(e.target.value)} />

        <button onClick={handleSubmit}>Submit</button>
        <button onClick={handleSkip}>Skip</button>
      </div>
    </>
  );
}

export default EducationForm;
