import React, { useState, useEffect, useRef } from 'react';

export default function VirtualStudyBuddy() {
  const [time, setTime] = useState(1500);
  const [isRunning, setIsRunning] = useState(false);
  const [tasks, setTasks] = useState([{ name: 'Complete Math Homework', completed: false }]);
  const [notes, setNotes] = useState([]);
  const [noteInput, setNoteInput] = useState('');
  const [quote, setQuote] = useState('The secret of getting ahead is getting started.');
  const intervalRef = useRef(null);

  const quotes = [
    "The secret of getting ahead is getting started.",
    "You don’t have to be great to start, but you have to start to be great.",
    "Success is the sum of small efforts, repeated day in and day out.",
    "The future depends on what you do today.",
    "Believe you can and you're halfway there.",
    "Don’t watch the clock; do what it does. Keep going.",
    "Difficulties in life are intended to make us better, not bitter.",
    "A little progress each day adds up to big results.",
    "Failure is the opportunity to begin again more intelligently.",
    "Your only limit is your mind. Keep pushing forward!"
  ];

  useEffect(() => {
    if (isRunning) {
      intervalRef.current = setInterval(() => {
        setTime(prev => {
          if (prev <= 1) {
            clearInterval(intervalRef.current);
            alert("Time’s up! Great job!");
            setIsRunning(false);
            return 1500;
          }
          return prev - 1;
        });
      }, 1000);
    }
    return () => clearInterval(intervalRef.current);
  }, [isRunning]);

  const formatTime = () => {
    const minutes = Math.floor(time / 60);
    const seconds = time % 60;
    return `${minutes}:${seconds < 10 ? '0' : ''}${seconds}`;
  };

  const startTimer = () => setIsRunning(true);
  const pauseTimer = () => {
    clearInterval(intervalRef.current);
    setIsRunning(false);
  };
  const resetTimer = () => {
    pauseTimer();
    setTime(1500);
  };

  const addTask = () => {
    const taskName = prompt("Enter task name:");
    if (taskName?.trim()) {
      setTasks([...tasks, { name: taskName.trim(), completed: false }]);
    } else {
      alert("Task name cannot be empty!");
    }
  };

  const toggleTask = index => {
    const updated = tasks.map((task, i) => i === index ? { ...task, completed: !task.completed } : task);
    setTasks(updated);
  };

  const clearCompleted = () => {
    setTasks(tasks.filter(task => !task.completed));
  };

  const updateProgress = () => {
    const completed = tasks.filter(task => task.completed).length;
    return tasks.length ? (completed / tasks.length) * 100 : 0;
  };

  const newQuote = () => {
    const random = quotes[Math.floor(Math.random() * quotes.length)];
    setQuote(random);
  };

  const saveNote = () => {
    if (noteInput.trim()) {
      setNotes([...notes, noteInput]);
      setNoteInput('');
    }
  };

  const clearNotes = () => setNotes([]);

  return (
    <div className="p-6 bg-gradient-to-br from-gray-100 to-blue-100 min-h-screen text-gray-800">
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 max-w-7xl mx-auto">
        <div className="space-y-6">
          <div className="bg-white p-6 rounded-2xl shadow">
            <h1 className="text-2xl font-bold text-blue-900 mb-4">Study Buddy</h1>

            <h2 className="text-lg font-semibold mb-2">Today's Progress</h2>
            <div className="w-full h-4 bg-gray-200 rounded-full overflow-hidden">
              <div className="h-full bg-green-500 transition-all" style={{ width: `${updateProgress()}%` }}></div>
            </div>
            <p className="text-sm mt-1">{Math.round(updateProgress())}% Complete</p>

            <h2 className="text-lg font-semibold mt-6 mb-2">Motivation</h2>
            <p className="italic text-gray-600 mb-2">{quote}</p>
            <button className="bg-blue-500 text-white px-4 py-1 rounded-full" onClick={newQuote}>New Quote</button>
          </div>
        </div>

        <div className="md:col-span-2 space-y-6">
          <div className="bg-white p-6 rounded-2xl shadow text-center">
            <h2 className="text-xl font-semibold mb-4">Study Timer</h2>
            <div className="text-4xl text-red-500 font-bold mb-4">{formatTime()}</div>
            <div className="flex justify-center gap-4 flex-wrap">
              <button className="bg-green-500 text-white px-4 py-1 rounded-full" onClick={startTimer}>Start</button>
              <button className="bg-gray-300 px-4 py-1 rounded-full" onClick={pauseTimer}>Pause</button>
              <button className="bg-gray-300 px-4 py-1 rounded-full" onClick={resetTimer}>Reset</button>
            </div>
          </div>

          <div className="bg-white p-6 rounded-2xl shadow">
            <h2 className="text-xl font-semibold mb-4">Task Manager</h2>
            <div className="flex gap-2 mb-4">
              <button className="bg-blue-500 text-white px-4 py-1 rounded-full" onClick={addTask}>Add Task</button>
              <button className="bg-gray-300 px-4 py-1 rounded-full" onClick={clearCompleted}>Clear Completed</button>
            </div>
            <ul>
              {tasks.map((task, idx) => (
                <li key={idx} className="flex items-center gap-2 mb-2">
                  <input type="checkbox" checked={task.completed} onChange={() => toggleTask(idx)} />
                  <span className={task.completed ? 'line-through text-gray-500' : ''}>{task.name}</span>
                </li>
              ))}
            </ul>
          </div>

          <div className="bg-white p-6 rounded-2xl shadow">
            <h2 className="text-xl font-semibold mb-4">Notes</h2>
            <textarea
              className="w-full border p-2 rounded mb-2"
              rows={5}
              value={noteInput}
              onChange={e => setNoteInput(e.target.value)}
              placeholder="Write your notes here..."
            ></textarea>
            <div className="flex gap-2 mb-2">
              <button className="bg-green-500 text-white px-4 py-1 rounded-full" onClick={saveNote}>Save Note</button>
              <button className="bg-gray-300 px-4 py-1 rounded-full" onClick={clearNotes}>Clear Notes</button>
            </div>
            <h3 className="text-lg font-semibold mb-2">Saved Notes:</h3>
            <ul className="list-disc list-inside">
              {notes.map((note, idx) => (
                <li key={idx}>{note}</li>
              ))}
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
}
