import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';

function Signup() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleSignup = () => {
    console.log('User signed up:', email, password);
    navigate('/login');
  };

  return (
    <div style={styles.background}>
      <div style={styles.card}>
        <h2 style={styles.heading}>ðŸš€ Create Your Account</h2>
        <p style={styles.subheading}>Let the adventure begin!</p>

        <input
          type="email"
          placeholder="ðŸ“§ Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          style={styles.input}
        /><br />

        <input
          type="password"
          placeholder="ðŸ”’ Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          style={styles.input}
        /><br />

        <div style={styles.rememberContainer}>
          <input type="checkbox" id="remember" />
          <label htmlFor="remember" style={styles.rememberLabel}>Remember me</label>
        </div>

        <button style={styles.button} onClick={handleSignup}>âš¡ Sign Up</button>

        <p style={styles.signupText}>
          Already have an account? <Link to="/login" style={styles.link}>Login</Link>
        </p>
      </div>
    </div>
  );
}

const styles = {
  background: {
    height: '100vh',
    background: 'linear-gradient(135deg, #ff758c, #ff7eb3)',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    fontFamily: "'Segoe UI', sans-serif",
  },
  card: {
    background: 'rgba(255, 255, 255, 0.15)',
    boxShadow: '0 8px 32px rgba(31, 38, 135, 0.37)',
    backdropFilter: 'blur(10px)',
    borderRadius: '20px',
    padding: '40px',
    textAlign: 'center',
    color: '#fff',
    width: '350px',
  },
  heading: {
    marginBottom: '10px',
    fontSize: '28px',
  },
  subheading: {
    fontSize: '14px',
    marginBottom: '20px',
    opacity: 0.8
  },
  input: {
    padding: '12px',
    margin: '10px 0',
    width: '100%',
    borderRadius: '8px',
    border: 'none',
    outline: 'none',
    fontSize: '16px',
  },
  rememberContainer: {
    textAlign: 'left',
    marginBottom: '10px',
  },
  rememberLabel: {
    marginLeft: '8px',
    fontSize: '14px',
    opacity: 0.9,
  },
  button: {
    padding: '12px 20px',
    border: 'none',
    borderRadius: '8px',
    fontSize: '16px',
    cursor: 'pointer',
    backgroundColor: '#ffffff',
    color: '#ff758c',
    fontWeight: 'bold',
    transition: '0.3s',
    width: '100%',
    marginTop: '10px'
  },
  signupText: {
    marginTop: '20px',
    fontSize: '14px',
  },
  link: {
    color: '#fff',
    textDecoration: 'underline',
  }
};

export default Signup;
