import React from 'react';
import { useNavigate, Link } from 'react-router-dom';

function Login() {
  const navigate = useNavigate();

  const handleLogin = () => {
    navigate('/dashboard');
  };

  return (
    <div style={styles.background}>
      <div style={styles.card}>
        <h2 style={styles.heading}>üîê Welcome Back</h2>
        <p style={styles.subheading}>Login to continue</p>

        <input type="email" placeholder="üìß Email" style={styles.input} /><br />
        <input type="password" placeholder="üîí Password" style={styles.input} /><br />

        <button style={styles.button} onClick={handleLogin}>Login</button>

        <p style={styles.signupText}>
          Don't have an account? <Link to="/signup" style={styles.link}>Sign up</Link>
        </p>
      </div>
    </div>
  );
}

const styles = {
  background: {
    height: '100vh',
    background: '#121212',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    fontFamily: "'Segoe UI', sans-serif",
  },
  card: {
    background: '#1e1e1e',
    boxShadow: '0 8px 24px rgba(0, 0, 0, 0.6)',
    borderRadius: '20px',
    padding: '40px',
    textAlign: 'center',
    color: '#fff',
    width: '350px',
  },
  heading: {
    marginBottom: '10px',
    fontSize: '28px',
  },
  subheading: {
    fontSize: '14px',
    marginBottom: '20px',
    color: '#aaa',
  },
  input: {
    padding: '12px',
    margin: '10px 0',
    width: '100%',
    borderRadius: '8px',
    border: '1px solid #333',
    background: '#2a2a2a',
    color: '#fff',
    fontSize: '16px',
  },
  button: {
    padding: '12px 20px',
    border: 'none',
    borderRadius: '8px',
    fontSize: '16px',
    cursor: 'pointer',
    backgroundColor: '#03dac6',
    color: '#000',
    fontWeight: 'bold',
    width: '100%',
    marginTop: '10px',
    transition: '0.3s',
  },
  signupText: {
    marginTop: '20px',
    fontSize: '14px',
  },
  link: {
    color: '#03dac6',
    textDecoration: 'underline',
  }
};

export default Login;
