import React, { useState, useEffect } from 'react';

function Dashboard() {
  const [name, setName] = useState('');
  const [task, setTask] = useState('');
  const [timer, setTimer] = useState(0);
  const [active, setActive] = useState(false);

  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem('user'));
    if (storedUser) setName(storedUser.name);
  }, []);

  useEffect(() => {
    let interval;
    if (active && timer > 0) {
      interval = setInterval(() => setTimer((t) => t - 1), 1000);
    } else {
      clearInterval(interval);
    }
    return () => clearInterval(interval);
  }, [active, timer]);

  const startTimer = () => {
    if (timer > 0) setActive(true);
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.heading}>üëã Hello, {name}!</h1>
      <input
        type="text"
        placeholder="Enter your task"
        value={task}
        onChange={(e) => setTask(e.target.value)}
        style={styles.input}
      />
      <input
        type="number"
        placeholder="Set timer (in seconds)"
        onChange={(e) => setTimer(parseInt(e.target.value))}
        style={styles.input}
      />
      <button onClick={startTimer} style={styles.button}>Start Timer</button>
      {timer > 0 && <h3 style={styles.timer}>‚è≥ {timer} sec left</h3>}
    </div>
  );
}

const styles = {
  container: {
    background: 'linear-gradient(135deg, #0f2027, #203a43, #2c5364)',
    height: '100vh',
    color: '#fff',
    padding: '40px',
    textAlign: 'center',
    fontFamily: 'Segoe UI',
  },
  heading: {
    fontSize: '32px',
    marginBottom: '20px',
  },
  input: {
    padding: '10px',
    margin: '10px',
    width: '300px',
    borderRadius: '8px',
    border: 'none',
    background: '#1e2a38',
    color: '#fff',
    fontSize: '16px',
  },
  button: {
    padding: '10px 20px',
    borderRadius: '8px',
    border: 'none',
    background: '#00b4db',
    color: '#fff',
    fontWeight: 'bold',
    cursor: 'pointer',
  },
  timer: {
    marginTop: '20px',
    fontSize: '24px',
  }
};

export default Dashboard;
