import React, { useState, useEffect } from 'react';

function Dashboard() {
  const [timeLeft, setTimeLeft] = useState(1500); // 25 minutes

  useEffect(() => {
    const timer = setInterval(() => {
      setTimeLeft((prev) => (prev > 0 ? prev - 1 : 0));
    }, 1000);
    return () => clearInterval(timer);
  }, []);

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  return (
    <div style={styles.background}>
      <div style={styles.card}>
        <h1 style={styles.heading}>üëã Hello, Subbulakshmi!</h1>
        <p style={styles.subheading}>Here‚Äôs your task for today:</p>
        <div style={styles.taskBox}>
          <h3>üìö Task: Complete React Routing Integration</h3>
          <p>‚è≥ Time Left: <span style={styles.timer}>{formatTime(timeLeft)}</span></p>
        </div>
      </div>
    </div>
  );
}

const styles = {
  background: {
    height: '100vh',
    background: 'linear-gradient(135deg, #0f2027, #203a43, #2c5364)',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    fontFamily: "'Segoe UI', sans-serif",
    color: '#fff',
  },
  card: {
    background: 'rgba(0, 0, 0, 0.4)',
    boxShadow: '0 8px 32px rgba(0, 0, 0, 0.37)',
    backdropFilter: 'blur(10px)',
    borderRadius: '20px',
    padding: '40px',
    textAlign: 'center',
    width: '500px',
  },
  heading: {
    fontSize: '28px',
    marginBottom: '10px',
    fontWeight: 'bold',
  },
  subheading: {
    fontSize: '16px',
    color: '#ccc',
    marginBottom: '20px',
  },
  taskBox: {
    background: '#1e2a38',
    padding: '20px',
    borderRadius: '12px',
    boxShadow: '0 0 10px rgba(0,0,0,0.4)',
  },
  timer: {
    fontSize: '24px',
    color: '#00b4db',
    fontWeight: 'bold',
  }
};

export default Dashboard;
