import React, { useState } from 'react';
import './Dashboard.css';

const Dashboard = ({ username }) => {
  const [subjects, setSubjects] = useState([]);
  const [newSubject, setNewSubject] = useState('');
  const [notesMap, setNotesMap] = useState({});
  const [selectedSubject, setSelectedSubject] = useState(null);
  const [newNote, setNewNote] = useState('');

  const handleAddSubject = () => {
    if (newSubject.trim() !== '') {
      setSubjects([...subjects, newSubject]);
      setNotesMap({ ...notesMap, [newSubject]: [] });
      setNewSubject('');
    }
  };

  const handleAddNote = () => {
    if (newNote.trim() !== '' && selectedSubject) {
      const updatedNotes = [...(notesMap[selectedSubject] || []), newNote];
      setNotesMap({ ...notesMap, [selectedSubject]: updatedNotes });
      setNewNote('');
    }
  };

  return (
    <div className="dashboard-container">
      <nav>
        <div className="logo">âš¡ AI Study Buddy</div>
        <div>
          <span className="welcome-text">Welcome, {username}!</span>
        </div>
      </nav>

      <div className="dashboard-panels">
        {/* Left Panel */}
        <div className="panel left-panel">
          <h3>Subjects</h3>
          <input
            type="text"
            placeholder="Enter subject"
            value={newSubject}
            onChange={(e) => setNewSubject(e.target.value)}
          />
          <button onClick={handleAddSubject}>Add Subject</button>
          <div className="items-list">
            {subjects.map((subj, idx) => (
              <div
                key={idx}
                className={`card ${selectedSubject === subj ? 'active' : ''}`}
                onClick={() => setSelectedSubject(subj)}
              >
                {subj}
                <div className="note-list">
                  {(notesMap[subj] || []).map((note, noteIdx) => (
                    <div key={noteIdx} className="note-item">â€¢ {note}</div>
                  ))}
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Middle Panel */}
        <div className="panel middle-panel">
          <h2>Hello ðŸ‘‹</h2>
          <p>Welcome to your study space. Select a subject to view/add notes.</p>
        </div>

        {/* Right Panel */}
        <div className="panel right-panel">
          <h3>Quick Notes</h3>
          {selectedSubject ? (
            <>
              <input
                type="text"
                placeholder="Enter a note for selected subject"
                value={newNote}
                onChange={(e) => setNewNote(e.target.value)}
              />
              <button onClick={handleAddNote}>Add Note</button>
              <div className="items-list">
                {(notesMap[selectedSubject] || []).map((note, idx) => (
                  <div key={idx} className="card">
                    {note}
                  </div>
                ))}
              </div>
            </>
          ) : (
            <p style={{ color: '#cbd5e1' }}>Select a subject to add/view notes</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
